galaxies = []
xcoords = []
ycoords = []
for i, line in enumerate(input):
    for j, char in enumerate(line):
        if char == '#':
            galaxies.append([i, j])
            xcoords.append(i)
            ycoords.append(j)

expandRows = []
for i in range(len(input)):
    if i not in xcoords:
        expandRows.append(i)

expandColumns = []
for j in range(len(input[0])):
    if j not in ycoords:
        expandColumns.append(j)

for row in reversed(expandRows):
    for i, x in enumerate(xcoords):
        if x > row:
            xcoords[i] += 1
for column in reversed(expandColumns):
    for j, y in enumerate(ycoords):
        if y > column:
            ycoords[j] += 1
total = 0
for i in range(len(xcoords)):
    for j in range(i+1, len(xcoords)):
        distance = abs(xcoords[i]-xcoords[j]) + abs(ycoords[i]-ycoords[j])
        total += distance
# print(xcoords)
